	/**
	 * 
	 * @Title: DetectFace   
	 * @Description: 人脸检测  
	 * @param: @param byte[]
	 * @param: @return 
	 * @author: JianLinWei     
	 * @return: String      
	 * @throws
	 */
	public static String DetectFace(byte[] photo) {
		try{
	/*InputStream  iStream  = new ByteArrayInputStream(photo);
		 BufferedImage bImage = ImageIO.read(iStream);
		iStream.close();
		
		  mat =  Mat2BufImg.BufImg2Mat(bImage, bImage.getType(), Cv);*/
			/*Vector<Byte> v  = new Vector<Byte>(photo.length);*/
			Mat  mat1 = new MatOfByte(photo);
          mat  = Imgcodecs.imdecode(mat1, Imgcodecs.CV_LOAD_IMAGE_COLOR);
			/*mat = new Mat(240, 320, CvType.CV_8UC3);
			mat.put(0, 0, photo);*/
		 imgdata = new byte[(int) (mat.total() * mat.channels())];
		mat.convertTo(mat, CvType.CV_8UC3);
		mat.get(0, 0, imgdata);
		
		
		 ljface = fa.LjDetect(handle, LjImageType.LJ_IMAGE_BGR8UC3.getValue(), imgdata, mat.width(),
				mat.height());
		if(ljface.size() <= 0){
			return "没有检测到人脸,请重新上传";
		}else if(ljface.size() >1){
			return "检测到多张人脸,请重新上传";
		}else{
			if(  !(ljface.get(0).yaw > -15 && ljface.get(0).yaw < 15 && 
					ljface.get(0).pitch > -15 && ljface.get(0).pitch < 15 && (ljface.get(0).rect.right - ljface.get(0).rect.left )> 70)  ){
				return "人脸太小或不是正脸,请摆正姿势重新上传";
			}
		}
		}catch(Exception e){
			e.printStackTrace();
			return e.getMessage();
			
		}
		
		return null;
	}
	
	
