	public static String  img_base64(byte[]  photo){
		Base64.Encoder  en  = Base64.getEncoder();

		String img_base= "";
		if( photo!=null && photo.length != 0){
			String base64 = en.encodeToString(photo);
			img_base =  "<img  id="+UUID.randomUUID()+"  class=\"getimg\"  onclick=\"takeImg(this)\"  height=\"60px\" src=\" " + "data:image/jpeg;base64,"
					+ base64 + "\">";
		}else{
			img_base = "无";
		}
		
		return img_base;
	}
	
	public static String  img_base64_head(byte[]  photo){
		Base64.Encoder  en  = Base64.getEncoder();

		String img_base= "";
		if( photo!=null && photo.length != 0){
			String base64 = en.encodeToString(photo);
			img_base =  "data:image/png;base64,"+base64;
		}else{
			img_base = "无";
		}
		
		return img_base;
	}
	public static byte[]  Base642Byte(String base){
		if(StringUtils.isEmpty(base))
			return null;
		if(base.contains("data:image/jpeg;base64,"))
			base = base.replace("data:image/jpeg;base64,", "");
		if(base.contains("data:image/png;base64,"))
			base = base.replace("data:image/png;base64,", "");
		
		Base64.Decoder  de = Base64.getDecoder();
		return de.decode(base);
	}
